$(document).ready(function () {
    $(".summernote-config").summernote({
        lang: 'es-ES',
        height: 100,
        toolbar: [
            // [groupName, [list of button]]
            ['view', ['fullscreen', 'codeview']],
            ['style', ['bold', 'italic', 'underline', 'clear']],
            ['font', ['strikethrough', 'superscript', 'subscript']],
            ['fontsize', ['fontsize']],
            ['color', ['color']],
            ['para', ['ul', 'ol', 'paragraph']],
            ['height', ['height']]            
        ]
    })
});