    function submitForm(formulario,notificacion){

        $.ajax({
            type: $(formulario).attr('method'), 
            url: $(formulario).attr('action'),
            data: $(formulario).serialize(),
            error : function(jqXHR, textStatus){
                if (jqXHR.status === 0) {
          
                alert('Not connect: Verify Network.');
            
                } else if (jqXHR.status == 404) {
            
                alert('Requested page not found [404]');
            
                } else if (jqXHR.status == 500) {
            
                alert('Internal Server Error [500].');
            
                } else if (textStatus === 'parsererror') {
            
                alert('Requested JSON parse failed.');
            
                } else if (textStatus === 'timeout') {
            
                alert('Time out error.');
            
                } else if (textStatus === 'abort') {
            
                alert('Ajax request aborted.');
            
                } else {
            
                alert('Uncaught Error: ' + jqXHR.responseText);
            
                }
            },
        }).then(function(data) {

            $(notificacion).hide();

            if(data.error!=null){
                $(notificacion).removeClass();
                $(notificacion).show();
                $(notificacion).addClass('alert alert-danger alert-dismissible bg-danger text-white border-0 fade show');

                $errores = '';
                for (let index = 0; index < data.error.length; index++) {
                    $error = '<li>'+data.error[index]+'</li>';
                    $errores +=$error;                    
                }

                $(notificacion).html('<h4 Class = "text-white">Completar Campos:</h4>'
                    +'<div class="row">'
                    +'<div class="col-lg-9 order-firts">'
                    +'<ul>'+$errores+'</ul>'
                    +'</div>'
                    +'<div class="col-lg-3 order-last text-center">'
                    +'<li class="fa fa-exclamation-triangle fa-7x"></li>'
                    +'</div>'
                    +'</div>'
                    +'<button type="button" class="close" data-dismiss="alert"'
                    +' aria-label="Close"> <span aria-hidden="true">&times;</span></button>  '
                );
            }else{
                if(data.mensaje!=null && data.error==null){
                    $(notificacion).removeClass();
                    $(notificacion).show();
                    $(notificacion).addClass('alert alert-success alert-dismissible bg-success text-white border-0 fade show');
                    $registro = data.mensaje[0];
                    $(notificacion).html(
                        +'<div class="row">'
                        +'<div class="col-lg-9 order-firts">'
                        +'<h4 Class = "text-white">'+$registro+'</h4>'
                        +'</div>'
                        +'<div class="col-lg-3 order-last text-center">'
                        +'<li class="fa fa-check fa-5x"></li>'
                        +'</div>'
                        +'</div>'
                        +'<button type="button" class="close" data-dismiss="alert"'
                        +' aria-label="Close"> <span aria-hidden="true">&times;</span></button>  '
                    );
                    $(formulario)[0].reset();
                }
            }
        });
    }